<?phpnamespace frontend\models;use common\models\BookChapter;use yii\base\Model;use Yii;class BookChapterForm extends Model {    //通过book-chapter的id获取文章    public function getChapter($id){        $chapterData = BookChapter::find()->where(['id' => $id])->asArray()->one();        if (!$chapterData){            return false;        }        //浏览次数自增1        /*$chapterObj = BookChapter::findOne($id);        $chapterObj->updateCounters(['browser' => 1]);*/        return $chapterData;    }    //通过book-chapter的book-id及catalog-id获取文章    public function getChapterByCatalogId($catalog){        $chapterData = BookChapter::find()            ->where(['book_id' => $catalog['book_id'], 'catalog_id' => $catalog['catalog_id']])            ->asArray()->one();        if (!$chapterData){            return false;        }        return $chapterData;    }    //通过book-id获取目录结构    public function getCatalogByBookId($bookId){        $catalogData = BookChapter::find()            ->select(['id', 'title', 'book_id', 'catalog_id'])            ->where(['book_id' => $bookId])            ->orderBy(['catalog_id' => SORT_ASC])            ->asArray()->all();        if (!$catalogData){            return false;        }        return $catalogData;    }}