<?php/** * Created by PhpStorm. * User: wayne * Date: 2016/12/10 * Time: 17:14 */namespace frontend\models;use common\models\Book;use yii\base\Model;class BookForm extends Model {    //获取指定书籍的信息    public function getBookInfo($book_id){        $bookInfo = Book::find()->where(['book_id' => $book_id])->asArray()->one();        if (!$bookInfo){            return false;        }        return $bookInfo;    }}