<?php/** * Created by PhpStorm. * User: wayne * Date: 2016/12/7 * Time: 0:40 */namespace frontend\models;use common\models\RelationBaikeTag;use yii\base\Model;class RelationBaikeTagForm extends Model {    public static function getSearchByTag($tagId){        $searchData = RelationBaikeTag::find()            ->where(['tag_id' => $tagId])            ->with('baike')  //通过RelationBaikeTag中的baike_id获取对应的baike数据            ->limit(15)            ->asArray()->all();        foreach ($searchData as $k => $item) {            $searchData[$k] = $item['baike'][0];        }        return $searchData;    }}