<?php/** * Created by PhpStorm. * User: wayne * Date: 2016/12/3 * Time: 21:58 */namespace frontend\models;use common\models\Tags;use yii\base\Exception;use yii\base\Model;class TagsForm extends Model {    public $tags;    public function rules()    {        return [            ['tags', 'required'],            ['tags', 'each', 'rule'=>['string']],        ];    }    public function saveTags(){        $ids = [];        if (!empty($this->tags)){            foreach ($this->tags as $tag){                $ids[] = $this->_saveTag($tag);            }        }        return $ids;    }    public function _saveTag($tag){        $model = new Tags();        $res = $model->find()->where(['tag_name' => $tag])->one();        if (!$res){            $model->tag_name = $tag;            $model->baike_num = 1;            if (!$model->save()){                throw new Exception('标签保存失败');            }            return $model->id;        }else{            $res->updateCounters(['baike_num' => 1]);            return $res->id;        }    }}