<?php/** * Created by PhpStorm. * User: wayne * Date: 2016/12/23 * Time: 17:20 */namespace frontend\models;use yii\base\Model;use common\models\User;class UserForm extends Model {    public static function getUserInfo(){        $userInfo = User::find()->select(['id', 'username', 'avatar', 'message_num', 'point'])            ->where(['id' => \Yii::$app->user->id])->asArray()->one();        return $userInfo;    }    public function getAvatar($userName){        $avatar = User::find()->select(['avatar'])->where(['username' => $userName])->asArray()->one();        return $avatar['avatar'];    }    public function getNameAndAvatar($userId){        $userInfo = User::find()->select(['username', 'avatar'])->where(['id' => $userId])->asArray()->one();        return $userInfo;    }}