<style>    .blog-edit-box {        margin-bottom: 50px;    }    form > div {        margin: 40px auto;    }    .create-tags {        width: 100%;        margin:10px auto;    }    .create-tags span {        color: #ffffff;        background: #52D5F1;        margin: 5px;        padding: 3px 5px;        border: 1px solid #cccccc;        border-radius: 3px;    }    .tag-clear {        vertical-align: top;        cursor: pointer;    }</style><div class="row blog-edit-box">    <div class="col-lg-9">        <h2>博文编辑</h2>        <span style="display: none;" id="blog-bucket"><?= Yii::$app->params['blogBucket']?></span>        <span style="display: none;" id="blog-domain"><?= Yii::$app->params['blogDomain']?></span>        <div class="active-form-box">            <?php $form = \yii\bootstrap\ActiveForm::begin()?>            <?= $form->field($model, 'title')->textInput(['maxlength'=>true])?>            <div class="form-group field-baikeform-content required">                <label class="control-label" for="baikeform-content">编辑内容</label>                <textarea id="baikeform-content" class="form-control" name="BaikeForm[content]" style="height:400px;max-height:800px;"></textarea>                <p class="help-block help-block-error"></p>            </div>            <?= $form->field($model, 'tags')->hiddenInput()->label(false)?> <!--此input的value是通过js动态加载-->            <p>可输入标签，多个标签以 <strong>空格隔开：</strong></p>            <div class="create-tags"></div>   <!--显示给用户看的tags-->            <!--用户操作的input，添加单条tags-->            <div class="input-group">                <span class="input-group-addon glyphicon glyphicon-tags"></span>                <input id="tag-input" type="text" class="form-control" placeholder="可输入标签（多个标签以空格隔开）">            </div>            <?= $form->field($model, 'is_valid')->radioList(['1' => '发布', '0' => '暂不发布'])?>            <?= $form->field($model, 'verifyCode')->widget(\yii\captcha\Captcha::className())?>            <?= \yii\bootstrap\Html::submitButton('提交', ['class'=>'btn btn-success'])?>            <?php \yii\bootstrap\ActiveForm::end()?>        </div>    </div></div><?php $this->registerCssFile('@web/static/wang/wangEditor/css/wangEditor.min.css', ['depends' => 'frontend\assets\SimpleAsset']) ?><?php $this->registerJsFile('@web/static/wang/wangEditor/js/wangEditor.min.js', ['depends' => 'frontend\assets\SimpleAsset']) ?><?php $this->registerJsFile('//cdn.bootcss.com/plupload/2.1.1/plupload.full.min.js', ['depends' => 'frontend\assets\SimpleAsset']) ?><?php $this->registerJsFile('https://cdn.staticfile.org/plupload/3.0-beta1/i18n/zh_CN.js', ['depends' => 'frontend\assets\SimpleAsset']) ?><?php $this->registerJsFile('https://cdn.staticfile.org/qiniu-js-sdk/1.0.14-beta/qiniu.min.js', ['depends' => 'frontend\assets\SimpleAsset']) ?><?php $this->registerJsFile('@web/static/wang/main.js', ['depends' => 'frontend\assets\SimpleAsset']) ?>