<?phpuse yii\helpers\Url;use yii\helpers\Html;$this->title = Html::encode($post['title']);?><div class="row">   <div class="col-lg-8">       <div class="item-content">           <p class="post-id hidden"><?= $post['id']?></p>           <a username="<?= $post['username']?>" userid="<?= $post['user_id']?>" class="to-userpage-btn">               <img class="img-circle user-img" src="<?= $post['avatar']?>" alt="avatar">               <i class="fa fa-user"></i>&nbsp;&nbsp;<span class="post-username"><?= Html::encode($post['username'])?></span>&nbsp;&nbsp;&nbsp;&nbsp;<?= date('m-d h:i', $post['create_time'])?>           </a>           <h4 class="post-title"><?= $this->title; ?></h4>           <p><?= Html::encode($post['content'])?></p>           <?php if ($post['imgs']): ?>               <?php               $imgs = explode(';', $post['imgs']);               ?>               <div class="row detail-imgs">                   <?php foreach ($imgs as $img): ?>                       <div class="col-lg-4 col-md-4 col-sm-4">                           <img class="img-responsive" src="<?= $img?>" alt="post-img">                       </div>                   <?php endforeach;?>               </div>           <?php endif;?>           <div class="content-footer">               <span><a href="#reply-box" type="button" class="btn btn-default btn-xs reply-btn"><i class="fa fa-edit"></i>&nbsp;&nbsp;回复</a></span>               <?php if ($post['reply_num']): ?>                   <span><i class="fa fa-commenting-o"></i>&nbsp;&nbsp;<?= $post['reply_num']?></span>               <?php endif;?>               <?php if ($post['browser']): ?>                   <span><i class="fa fa-eye"></i>&nbsp;&nbsp;<?= $post['browser']?></span>               <?php endif;?>               <div class="clearfix"></div>           </div>       </div>       <!--回复内容-->       <div class="reply">           <?php foreach ($post['reply'] as $reply): ?>               <div class="reply-item" replyid="<?= $reply['id']?>">                   <hr class="hr-10">                   <a userid="<?= $reply['user_id']?>" username="<?= $reply['username']?>" class="to-userpage-btn">                       <img class="img-circle user-img" src="<?= $reply['avatar']?>" alt="avatar">                       <i class="fa fa-user"></i>&nbsp;&nbsp;<span class="username"> <?= $reply['username']?></span>&nbsp;&nbsp;&nbsp;&nbsp;<?= date('m-d h:i', $reply['time'])?>                   </a>                   <p><?= Html::encode($reply['content'])?></p>                   <div class="reply-footer">                       <span><button type="button" class="btn btn-default btn-xs reply-btn re-reply-btn"><i class="fa fa-edit"></i>&nbsp;&nbsp;回复</button></span>                       <?php if ($reply['re_reply_num']): ?>                           <span class="re_reply_num"><i class="fa fa-commenting-o"></i>&nbsp;&nbsp;<?= $reply['re_reply_num']?></span>                       <?php endif;?>                       <div class="clearfix"></div>                   </div>               </div>           <?php endforeach;?>           <hr>           <p class="add-reply">添加回复</p>           <div id="reply-box" class="padding-10">               <textarea class="form-control" rows="2" placeholder="回复" maxlength="255"></textarea>               <button class="btn btn-default btn-sm f-right margin-v-10 send-reply-btn" type="button"><i class="fa fa-paper-plane-o"></i>&nbsp;发送</button>               <div class="clearfix"></div>           </div>       </div>   </div>    <div class="col-lg-4">    </div></div>