<style>    .item-content {        margin: 10px;        padding: 10px;    }    .user-name {        font-size: larger;        font-weight: 500;    }    .user-img {        width: 50px;        height: 50px;        margin-right: 10px;    }    .item-content p {        color: #8c8c8c;        line-height: 1.5em;    }    .content-imgs {        margin-left: 10px;    }    .content-imgs img {        width: 80px;        margin: 5px;    }</style><?phpuse yii\helpers\Html;?><div class="item-content">    <div>        <img class="img-circle user-img" src="<?= Html::encode($post['avatar'])?>" alt="用户图像">        <span class="user-name"><?= Html::encode($post['username'])?>&nbsp;&nbsp;&nbsp;&nbsp;</span>        <span><?= date('m-d h:i', $post['create_time'])?></span>    </div>    <hr>    <h4><?= Html::encode($post['title'])?></h4>    <p>        <?= Html::encode($post['content'])?>    </p>    <?php if ($post['imgs']): ?>        <?php        $imgs = explode(';', $post['imgs']);        ?>        <div class="content-imgs">            <?php foreach ($imgs as $img): ?>                <img src="<?= $img?>" alt="post-img">            <?php endforeach;?>        </div>    <?php endif;?></div>