<?php/** * 商家视图 * Created by PhpStorm. * User: wayne * Date: 2016/11/16 * Time: 10:30 */namespace frontend\widgets\baike;use frontend\models\BaikeForm;use yii\base\Widget;class BaikeWidget extends Widget {    public $pageSize;    public $condition;    public function init()    {        parent::init(); // TODO: Change the autogenerated stub        if ($this->pageSize === null){            $this->pageSize = 5;        }    }    public function run(){        $res = BaikeForm::getBaike($this->pageSize, $this->condition);        return $this->render('index', ['baike' => $res['baike'], 'pagination' => $res['pagination']]);    }}