<?php/** * 商家视图 * Created by PhpStorm. * User: wayne * Date: 2016/11/16 * Time: 10:30 */namespace frontend\widgets\hosp;use frontend\models\HospForm;use yii\base\Widget;class HospWidget extends Widget {    public $limit;    public function init()    {        parent::init(); // TODO: Change the autogenerated stub        if ($this->limit === null){            $this->limit = 1;        }    }    public function run(){        //传参，在“百科-->医护”中显示一条        $hospData = (new HospForm())->getLocalHosp($this->limit);        return $this->render('index', ['hosps' => $hospData]);    }}