<?php/** * Created by PhpStorm. * User: wayne * Date: 2016/12/31 * Time: 8:32 */namespace frontend\widgets\post;use frontend\models\PostForm;use yii\base\Widget;class UserPostWidget extends Widget {    public $userId;    public function run(){        $res = (new PostForm())->getUserPost($this->userId);        return $this->render('index', ['posts' => $res['posts'], 'pagination' => $res['pagination']]);    }}